mapper.update:
update appraisal


        appraisal.content = #{appraisal.content,jdbcType=VARCHAR},


        appraisal.summary = #{appraisal.summary,jdbcType=VARCHAR},


        appraisal.corp_opinion = #{appraisal.corpOpinion,jdbcType=VARCHAR},


        appraisal.corp_teacher_opinion = #{appraisal.corpTeacherOpinion,jdbcType=VARCHAR}


where appraisal.stuno = #{appraisal.stuno,jdbcType=VARCHAR}
